import { Grid } from '@mui/material';
import React,{useState,useEffect, useRef} from 'react';
import { ReactMic } from 'react-mic';
import {HomeWrapper, RecordTitle} from './styled'
import Card from '@mui/material/Card';
import CardActions from '@mui/material/CardActions';
import CardContent from '@mui/material/CardContent';
import CardMedia from '@mui/material/CardMedia';
import Button from '@mui/material/Button';
import Typography from '@mui/material/Typography';
import ReactQuill from "react-quill"
import 'react-quill/dist/quill.snow.css'
import Loader from '../../Components/Loader';

const NewMeeting:React.FC = () =>{
    const [record,setRecord] = useState<boolean>(false)
    const [loader,setLoader] = useState<boolean>(false)
    const [blob,setBlob] = useState<any>(null)
    const [text,setText] = useState<string>('')
    const [newText, setNewText] = useState('During Independence, India was still one of the most populous countries with 350 million people. That was the reason it became the first developing country to start a family planning programme in 1951. Since then, the country’s population has quadrupled, with 1.37 billion people in 2019. Population scientists have postulated a threshold to the number of births to keep population under control. This is expressed as the total fertility rate (TFR), which is the average number of children a woman of childbearing age must have. Population above TFR means growth, while that below TFR means decline. At TFR, population is maintained. For Homo sapiens, a 2.1 TFR would keep the country’s population stable. The number accounts for one child per mother, one per father, and an extra 0.1 for children who die in infancy and women who die before childbearing age. The UN Population Division terms this as the replacement-level fertility. “If replacement-level fertility is sustained over a sufficiently long period, each generation will exactly replace itself without any need for the country to balance the population by international migration,” says the UN. India is very close to this point now, as many states have, in fact, TFR below 2.1. This means India’s population is about to hit the replacement level. Or, there will be no effective population growth. India’s official data suggests this. The National Family Health Survey (NFHS)-4, conducted in 2015-16, found India’s TFR had reached 2.2. Most Indian states had already achieved or were below 2.1 TFR. Himachal Pradesh, West Bengal, Maharashtra, Punjab, Tamil Nadu, Kerala, Karnataka and Andhra Pradesh had TFR well below the replacement level. The exceptions were Bihar (3.4), Uttar Pradesh (2.7), Jharkhand (2.6), Rajasthan (2.4), Madhya Pradesh (2.3), Chhattisgarh (2.2), Assam (2.2) and some north-eastern states. Darrell Bricker, author of the book Empty Planet that predicted an unprecedented global decline in fertility, also says: “India’s TFR has already reached replacement rate.” Demographer and social scientist Shireen Jejeebhoy writes that 17 of the 28 states and 8 out of 9 Union Territories have reached the replacement level. The Economic Survey 2018-19 tabled in Parliament and with a chapter on population, says, “India is set to witness a sharp slowdown in population growth in the next two decades.” According to it, population in the 0-19 age bracket has already peaked due to sharp decline in TFR across the country. The Economic Survey, in fact, suggested massive reorientation of public infrastructure like schools to prepare for less population. “Further, the national TFR will dip below the replacement level by 2021,” says Srinivas Goli, assistant professor of population studies at the Centre for the Study of Regional Development, Jawaharlal Nehru University, Delhi.');
    const [convertedText, setConvertedText] = useState('');
    const [meetingStart, setMeetingStart] = useState<boolean>(false);



    useEffect(()=>{
        if(meetingStart) setNewText(newText+' '+text)
    },[text])

    const textFormatter = (newText:string) =>{
        setText(newText)
    }

    const startRecording = () => {
        setRecord(true)
    }

     const stopRecording = () => {
        setRecord(false)
    }
    const onStop = async (recordedBlob:any) =>{
        var reader = new FileReader();
        reader.readAsDataURL(recordedBlob.blob);
        reader.onloadend = async  () =>{
            let base64String = reader.result;
            setBlob(base64String)
            const rawResponse = await fetch('http://localhost:3800/submit-audio', {
                method: 'POST',
                headers: {
                'Accept': 'application/json',
                'Content-Type': 'application/json',
                'Access-Control-Allow-Origin':'*'
                },
                body:JSON.stringify({a:base64String})
            });
        }

    }


    return <HomeWrapper container >
        <Grid item md={12}>
            <RecordTitle>SADSA</RecordTitle>
        </Grid>
        <Grid item md={12}>
            <ReactMic
            record={record}
            className="sound-wave"
            onStop={onStop}
            noiseSuppression={true}
            strokeColor="#fff"
            backgroundColor="#034acf" />
        </Grid>


    </HomeWrapper>
}

export default NewMeeting
