import React,{useState} from 'react';
import { ReactMediaRecorder ,useReactMediaRecorder} from "react-media-recorder";
import Webcam from "react-webcam";

const Home:React.FC = () =>{
    const {
        status,
        startRecording,
        stopRecording,
        mediaBlobUrl,
      } = useReactMediaRecorder({ video: true });
    const [start,setStart] = useState<Boolean>(false)
    const recordStart = () =>{
        setStart(true)
    }
    const recordStop = () =>{
        setStart(false)
    }
    console.log(mediaBlobUrl,'===mediaBlobUrl');



    return<>
    {
        start && <Webcam />
    }
        <ReactMediaRecorder
      video
      render={({ status, startRecording, stopRecording, mediaBlobUrl }) => (
        <div>
          <p>{status}</p>
          <button onClick={()=>{
              recordStart()
              startRecording()
          }}>Start Recording</button>
          <button onClick={()=>{
              console.log('asdasd');

              recordStop()
              stopRecording()
          }}>Stop Recording</button>
          {
        !start && <video src={mediaBlobUrl??''} controls autoPlay loop />
    }

        </div>
      )}
    />
    </>
}

export default Home
