import { Grid } from '@mui/material';
import React,{useState,useEffect, useRef} from 'react';
import { ReactMic } from 'react-mic';
import {HomeWrapper} from './styled'
import Card from '@mui/material/Card';
import CardActions from '@mui/material/CardActions';
import CardContent from '@mui/material/CardContent';
import CardMedia from '@mui/material/CardMedia';
import Button from '@mui/material/Button';
import Typography from '@mui/material/Typography';
import ReactQuill from "react-quill"
import 'react-quill/dist/quill.snow.css'
import Loader from '../../Components/Loader';

const Home:React.FC = () =>{
    const [record,setRecord] = useState<boolean>(false)
    const [loader,setLoader] = useState<boolean>(false)
    const [blob,setBlob] = useState<any>(null)
    const [text,setText] = useState<string>('')
    const [newText, setNewText] = useState('');
    const [convertedText, setConvertedText] = useState('');
    const [meetingStart, setMeetingStart] = useState<boolean>(false);



    useEffect(()=>{
        if(meetingStart) setNewText(newText+' '+text)
    },[text])
    const start = () =>{
        setMeetingStart(true)
        setLoader(true)
        navigator.mediaDevices.getUserMedia({audio: true}).then(stream=>{
            if(!MediaRecorder.isTypeSupported('audio/webm')) return alert('Not Supported')
            const mediaRecorder = new MediaRecorder(stream,{mimeType:'audio/webm'})
            const socket = new WebSocket('wss://api.deepgram.com/v1/listen?punctuate=true&version=latest&profanity_filter=true&numerals=true',['token','1347b873378522f6f0bde96d191fb52655d81ca9'])

            socket.onopen = () =>{
                setLoader(false)

                mediaRecorder.addEventListener('dataavailable',event =>{
                    socket.send(event.data)
                })
                mediaRecorder.start(100)
            }
            socket.onmessage = message =>{
                // console.log(message,'message');
                const data = JSON.parse(message.data)
                const transcript = data.channel.alternatives[0].transcript
                if(transcript && data.is_final){
                    textFormatter(transcript)
                }

            }

        })

    }
    const stop = () =>{
        setMeetingStart(false)
    }
    const textFormatter = (newText:string) =>{
        setText(newText)
    }

    const startRecording = () => {
        setRecord(true)
    }

     const stopRecording = () => {
        setRecord(false)
    }
    const onStop = async (recordedBlob:any) =>{
        console.log('recordedBlob is: ', recordedBlob);
        // const base64 =  await covertToBase64(recordedBlob.blob)
        // console.log(recordedBlob.blob,'====base64');
        var reader = new FileReader();
        reader.readAsDataURL(recordedBlob.blob);
        reader.onloadend = function () {
            let base64String = reader.result;
            setBlob(base64String)
        }

    }
    const submitForSummary = async() =>{
        let url = 'https://f2cf-35-238-80-252.ngrok.io?wholetext='+newText
        const rawResponse = await fetch(url, {
            method: 'POST',

            headers: {
            'Accept': 'application/json',
            'Content-Type': 'application/json',
            'Access-Control-Allow-Origin':'*'
            },
        });
        console.log(await rawResponse.json());

    }

    return <HomeWrapper container spacing={2}>
        {loader && <Loader />}
        {/* <ReactMic
          record={record}
          className="sound-wave"
          onStop={onStop}
          onData={onData}
          strokeColor="#000000"
          backgroundColor="#FF4081" />
        <button onClick={startRecording} type="button">Start</button>
        <button onClick={stopRecording} type="button">Stop</button> */}
        <Grid item md={6}>
            <Card >
                <CardMedia
                    component="img"
                    height="140"
                    image="https://previews.123rf.com/images/katedav/katedav1409/katedav140900264/31993569-%E6%BC%AB%E7%94%BB%E4%BC%9A%E8%AD%B0%E3%81%BE%E3%81%9F%E3%81%AF%E4%BC%9A%E8%AD%B0%E3%83%86%E3%83%BC%E3%83%96%E3%83%AB%E3%82%92%E3%83%A9%E3%82%A6%E3%83%B3%E3%83%89%E3%81%97%E3%81%BE%E3%81%99%E3%80%82.jpg"
                    alt="green iguana"
                />
                <CardContent>
                    <Typography gutterBottom variant="h5" component="div">
                        Record New Meeting
                    </Typography>
                    <Typography variant="body2" color="text.secondary">
                        Are you going to have a new meeting? Lets record and make a summary to people.
                    </Typography>
                </CardContent>
                <CardActions>
                    {!meetingStart && <Button size="small" onClick={start}>Start Recording</Button>}
                    {meetingStart && <Button size="small" onClick={stop}>Stop Recording</Button>}
                </CardActions>
            </Card>
        </Grid>
        <Grid item md={6}>
            <Card >
                <CardMedia
                    component="img"
                    height="140"
                    image="https://image.shutterstock.com/image-vector/stickman-illustration-featuring-group-teenage-260nw-683203834.jpg"
                    alt="green iguana"
                />
                <CardContent>
                    <Typography gutterBottom variant="h5" component="div">
                        MIssed a meeting?
                    </Typography>
                    <Typography variant="body2" color="text.secondary">
                        Let's give you some ideas what happened in last coupple of minutes. You will get some summary from it.
                    </Typography>
                </CardContent>
                <CardActions>
                    <Button size="small">Let's go</Button>
                </CardActions>
            </Card>
        </Grid>
        <Grid item md={12}>
        <ReactQuill
            theme='snow'
            value={newText}
            onChange={setConvertedText}
            style={{minHeight: '300px'}}
        />    
        </Grid>
        <Grid item md={12}>
            <Button size='large' variant='contained' onClick={submitForSummary}>Get Summary</Button> 
        </Grid>

    </HomeWrapper>
}

export default Home
